function [elts,target] = ReadContourStimuli(filename,stimulus_index)% stimulus_index is 0 basedfid = fopen(filename,'r','ieee-le');buffer = fread(fid,6,'int32');% buffer(1) corresponds to index of stimulus% buffer(2) is the number of gabors in each stimulus% buffer(3:6) are the coordinates of the rectange% of the targetwhile buffer(1)~=stimulus_index,	% skip to the start of the next stimulus, which is number of gabors * 12 bytes	% (2 int32's and 1 float32)	fseek(fid,buffer(2)*12,'cof');	buffer = fread(fid,6,'int32');endtarget = buffer(3:6);% start reading from here for number of gaborselts = zeros(buffer(2),3);for i=1:buffer(2),	elts(i,1:2) = transpose(fread(fid,2,'int32'));	elts(i,3) = fread(fid,1,'float32');endfclose(fid);